import React, { useState, useEffect } from 'react';

function Drinkware() {
    const [products, setProducts] = useState([]);
    const [selectedCategory, setSelectedCategory] = useState(null); // State to track the selected category
    const [isAdmin, setIsAdmin] = useState(false); // Assuming isAdmin state represents the user's role

    useEffect(() => {
        let url = 'http://localhost:3001/api/products';

        // If the user selects a category, append the category ID to the URL
        if (selectedCategory !== null) {
            url += `/${selectedCategory}`;
        }

        fetch(url)
            .then(response => response.json())
            .then(data => {
                // Filter products for standard users or use all products for admins
                const filteredProducts = isAdmin ? data : data.filter(product => product.category_id === selectedCategory);
                setProducts(filteredProducts);
            })
            .catch(error => console.error('Error fetching product data:', error));
    }, [selectedCategory, isAdmin]);

    return (
        <div>
            <h2>Drinkware</h2>
            {/* Assume you have some component to select categories and update the selectedCategory state */}
            {/* For example, a dropdown or buttons to select categories */}
            <div className="category-selector">
                {/* Your category selection component here */}
            </div>
            <div className="product-cards">
                {products.map(product => (
                    <div key={product.id} className="product-card">
                        <h3>{product.product_name}</h3>
                        <p>Price: ${product.price}</p>
                        <p>Stock: {product.stock}</p>
                        <img src={`/images/${product.image}`} alt={product.product_name} />

                        {/* Find dropdown component in bootstrap and allow users to select a size */}
                        <button>Add to Cart</button>
                        {/* Add additional dropdowns to select font style, color, etc... Would need to integrate into database models */}
                    </div>
                ))}
            </div>
        </div>
    );
}

export default Drinkware;
