import * as React from 'react';
import Container from 'react-bootstrap/Container';
import Nav from 'react-bootstrap/Nav';
import Navbar from 'react-bootstrap/Navbar';
import { Link } from 'react-router-dom';

const pages = ['Home', 'Products', 'Custom Orders', 'Cart', 'Login'];

function ResponsiveAppBar() {
    const [isLoggedIn, setIsLoggedIn] = React.useState(false); 

    handleLogout = () => {
        // Perform logout logic here
        setIsLoggedIn(false);

        // Redirect to the home page after logout
        window.location.href = '/';

        // Alternatively, use the useHistory hook from react-router-dom to redirect
        // to the home page after logout


    }

    function Navigation() {
        return (
            <div>
                <Navbar collapseOnSelect expand="lg" id="navBar">
                    <Container className='navContainer'>
                        <Navbar className="companyLogo" as={Link} to="/">Code 3 Graphix</Navbar>
                        <Navbar.Toggle aria-controls="responsive-navbar-nav" />
                        <Navbar.Collapse className='navbarLinks' id="responsive-navbar-nav">
                            <Nav>
                                {pages.map((page, index) => (
                                    <Nav.Link key={index} as={Link} to={page.toLowerCase().replace(/\s+/g, '')}>
                                        {page}
                                    </Nav.Link>
                                ))}
                            </Nav>
                        </Navbar.Collapse>
                    </Container>
                </Navbar>
            </div>
        );
    }

    return <Navigation />;
}

export default ResponsiveAppBar;
